//Print the sum of even numbers between 1 and 50
package Assignments;

public class Assignment7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
	
for(int i=0; i<=50; i++)
{
	if (i%2==0);
	{
	sum +=i;
	}
}
	System.out.println("This is the sum of the even numbers "+sum);

}
}

